public class BluetoothBondedDeviceHelper {

    private String name = "";
    private String devAddress = "";


    public BluetoothBondedDeviceHelper() {
        // TODO Auto-generated constructor stub
        super();
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getMAC_address() {
        return devAddress;
    }

    public void setMAC_address(String addr) {
        this.devAddress = addr;
    }








}
